
USE [$(dbName)]
Go

if not Exists (select EmailTemplateId from TblEmailTemplate where EmailTitle = 'TestUpsellNotification')
begin
	INSERT INTO [TblEmailTemplate]
			   ([EmailTitle],[EmailSubject],[EmailBody],[DateCreated],[DateModified])
	VALUES
	('TestUpsellNotification','Test Upsell Notification' 
		,'<table style="border-collapse: collapse; border-color: black; width: 600px;" border="1px" cellspacing="0px" cellpadding="0px">  <tbody>  <tr>  <td>  <table style="font-family: Arial; width: 600px;" border="0px" cellspacing="0px" cellpadding="10px">  <tbody>  <tr>  <td style="height: 143px;">  <table style="width: 600px;" border="0px" cellspacing="0px" cellpadding="10px">  <tbody>  <tr>  <td><img src="@Model.EmailCommunicationBaseInfo.EmailNotificationLogoRelativePath" alt="" /></td>  <td style="color: #47a942;" align="right">Upsell Email</td>  </tr>  </tbody>  </table>  </td>  </tr>  <tr>  <td style="font-size: 13px;">Dear @Model.CustomerName,<br /> <br /> I congratulate you on taking a proactive approach to your health by registering for a @Model.EmailCommunicationBaseInfo.CompanyName. As a physician and Chief Medical Director at HealthFair, I encourage our participants to take advantage of all testing that may be appropriate based on their personal risk factors. <br /> <br /> Upon review of the package you selected, I noticed that you may be missing out on a few important screenings. This includes: <br /><br /> @if(Model.Tests !=null && Model.Tests.Count() > 0)                        {                        <table border="1" cellpadding="5px" cellspacing="0px"  width="580px">                            @foreach(var test in Model.Tests)                            {                                <tr>                                    <td style="width:30%;">                                        @test.Name                                    </td>                                    <td style="width:70%;">                                        @test.Description                                    </td>                                </tr>                            }                        </table>                         }</td>  </tr>  <tr>  <td style="font-size: 13px;">I recommend that you consider adding these tests to your screening package. A more comprehensive examination will increase the likelihood of discovering an abnormality, thus allowing you to take action before disease progresses. <br /> <br /> To add a test to your appointment or to learn more about why these screenings are recommended for you, please speak with a screening consultant by calling 855-216-8378.</td>  </tr>  <tr>  <td style="font-size: 13px;">Wishing you the best of health,<br /> Donald Grossman MD, F.A.C.P.<br /> Chief Medical Director, HealthFair</td>  </tr>  <tr>  <td><hr style="float: left; height: 3px; width: 100%; color: #000; background-color: #000;" /></td>  </tr>  <tr>  <td class="normaltxt_pw" style="font-size: 12px;"><strong>@Model.EmailCommunicationBaseInfo.CompanyName</strong><br /> @Model.EmailCommunicationBaseInfo.CompanyAddress.StreetAddressLine1<br /> @Model.EmailCommunicationBaseInfo.CompanyAddress.StreetAddressLine2<br /> @Model.EmailCommunicationBaseInfo.CompanyAddress.City, @Model.EmailCommunicationBaseInfo.CompanyAddress.State&nbsp; @Model.EmailCommunicationBaseInfo.CompanyAddress.ZipCode<br /> <a href="@Model.EmailCommunicationBaseInfo.PrivacyPolicyUrl" target="_blank">Privacy Policy</a> | <a href="http://www.healthfair.com/FAQ/CancellationPolicy.aspx" target="_blank">Cancellation Policy</a><br /> <br /> <strong>THIS IS AN AUTOMATED MESSAGE</strong> - PLEASE DO NOT REPLY DIRECTLY TO THIS EMAIL. IF YOU WISH TO CONTACT HEALTHFAIR VIA EMAIL PLEASE USE <a href="mailto:INFO@HEALTHFAIR.COM">INFO@HEALTHFAIR.COM</a></td>  </tr>  </tbody>  </table>  </td>  </tr>  </tbody>  </table>'
		,getDate(),getDate())

end
else
begin
	update TblEmailTemplate 
	set [EmailBody] = '<table style="border-collapse: collapse; border-color: black; width: 600px;" border="1px" cellspacing="0px" cellpadding="0px">  <tbody>  <tr>  <td>  <table style="font-family: Arial; width: 600px;" border="0px" cellspacing="0px" cellpadding="10px">  <tbody>  <tr>  <td style="height: 143px;">  <table style="width: 600px;" border="0px" cellspacing="0px" cellpadding="10px">  <tbody>  <tr>  <td><img src="@Model.EmailCommunicationBaseInfo.EmailNotificationLogoRelativePath" alt="" /></td>  <td style="color: #47a942;" align="right">Upsell Email</td>  </tr>  </tbody>  </table>  </td>  </tr>  <tr>  <td style="font-size: 13px;">Dear @Model.CustomerName,<br /> <br /> I congratulate you on taking a proactive approach to your health by registering for a @Model.EmailCommunicationBaseInfo.CompanyName. As a physician and Chief Medical Director at HealthFair, I encourage our participants to take advantage of all testing that may be appropriate based on their personal risk factors. <br /> <br /> Upon review of the package you selected, I noticed that you may be missing out on a few important screenings. This includes: <br /><br /> @if(Model.Tests !=null && Model.Tests.Count() > 0)                        {                        <table border="1" cellpadding="5px" cellspacing="0px"  width="580px">                            @foreach(var test in Model.Tests)                            {                                <tr>                                    <td style="width:30%;">                                        @test.Name                                    </td>                                    <td style="width:70%;">                                        @test.Description                                    </td>                                </tr>                            }                        </table>                         }</td>  </tr>  <tr>  <td style="font-size: 13px;">I recommend that you consider adding these tests to your screening package. A more comprehensive examination will increase the likelihood of discovering an abnormality, thus allowing you to take action before disease progresses. <br /> <br /> To add a test to your appointment or to learn more about why these screenings are recommended for you, please speak with a screening consultant by calling 855-216-8378.</td>  </tr>  <tr>  <td style="font-size: 13px;">Wishing you the best of health,<br /> Donald Grossman MD, F.A.C.P.<br /> Chief Medical Director, HealthFair</td>  </tr>  <tr>  <td><hr style="float: left; height: 3px; width: 100%; color: #000; background-color: #000;" /></td>  </tr>  <tr>  <td class="normaltxt_pw" style="font-size: 12px;"><strong>@Model.EmailCommunicationBaseInfo.CompanyName</strong><br /> @Model.EmailCommunicationBaseInfo.CompanyAddress.StreetAddressLine1<br /> @Model.EmailCommunicationBaseInfo.CompanyAddress.StreetAddressLine2<br /> @Model.EmailCommunicationBaseInfo.CompanyAddress.City, @Model.EmailCommunicationBaseInfo.CompanyAddress.State&nbsp; @Model.EmailCommunicationBaseInfo.CompanyAddress.ZipCode<br /> <a href="@Model.EmailCommunicationBaseInfo.PrivacyPolicyUrl" target="_blank">Privacy Policy</a> | <a href="http://www.healthfair.com/FAQ/CancellationPolicy.aspx" target="_blank">Cancellation Policy</a><br /> <br /> <strong>THIS IS AN AUTOMATED MESSAGE</strong> - PLEASE DO NOT REPLY DIRECTLY TO THIS EMAIL. IF YOU WISH TO CONTACT HEALTHFAIR VIA EMAIL PLEASE USE <a href="mailto:INFO@HEALTHFAIR.COM">INFO@HEALTHFAIR.COM</a></td>  </tr>  </tbody>  </table>  </td>  </tr>  </tbody>  </table>'
	where EmailTitle = 'TestUpsellNotification'
end


	

	


